mapper.update:
update tbl_houseroom
set roomType=#{roomType}, roomGender=#{roomGender},
roomDeposit=#{roomDeposit}, roomMonthly=#{roomMonthly},
roomArea=#{roomArea}, roomDate=#{roomDate}
where
houseNumber=#{houseNumber};

